-- Create updated_at trigger function
CREATE OR REPLACE FUNCTION update_updated_at_column()
RETURNS TRIGGER AS $$
BEGIN
    NEW.updated_at = NOW();
    RETURN NEW;
END;
$$ language 'plpgsql';

-- Create triggers for updated_at
CREATE TRIGGER update_drum_kits_updated_at BEFORE UPDATE ON drum_kits
    FOR EACH ROW EXECUTE FUNCTION update_updated_at_column();

CREATE TRIGGER update_drum_patterns_updated_at BEFORE UPDATE ON drum_patterns
    FOR EACH ROW EXECUTE FUNCTION update_updated_at_column();

CREATE TRIGGER update_drum_sessions_updated_at BEFORE UPDATE ON drum_sessions
    FOR EACH ROW EXECUTE FUNCTION update_updated_at_column();

CREATE TRIGGER update_drum_kit_settings_updated_at BEFORE UPDATE ON drum_kit_settings
    FOR EACH ROW EXECUTE FUNCTION update_updated_at_column();
